        public void Setup(string user, string level, string itemCode, string weavingLot, string finishingLot, DateTime? entryDate, string status)
        {
            if (opera != null)
            {
                opera = user;
            }

            if (positionLevel != null)
            {
                positionLevel = level;
            }

            if (ITM_CODE != null)
            {
                ITM_CODE = itemCode;
            }

            if (WEAVINGLOT != null)
            {
                WEAVINGLOT = weavingLot;
            }

            if (FINISHINGLOT != null)
            {
                FINISHINGLOT = finishingLot;
            }

            if (ENTRYDATE != null)
            {
                ENTRYDATE = entryDate;
            }

            if (STATUS != null)
            {
                STATUS = status;
            }
